mapper.select:
/* FrontMapper.findCowPriceList */ SELECT B.LOCNM , A.MONTH_OLD_C , CASE WHEN A.MONTH_OLD_C = '01' THEN '4~5개월' WHEN A.MONTH_OLD_C = '02' THEN '6~7개월' WHEN A.MONTH_OLD_C = '03' THEN '8개월 이상' WHEN A.MONTH_OLD_C = '11' THEN '400kg 미만' WHEN A.MONTH_OLD_C = '12' THEN '400kg 이상' ELSE '전체' END AS MONTH_OLD_C_NM , SUM(NVL(A.TOT_SBID_CNT,0)) SUM_SBID_CNT , NVL(TRUNC((DECODE(SUM(A.TOT_SBID_CNT),0,0,(SUM(A.MAX_SBID_UPR) / SUM(A.TOT_SBID_CNT))))),0) AS AVG_SBID_AM FROM TB_LA_IS_MM_DB_SUM A LEFT JOIN TB_LA_IS_BM_BZLOC B ON A.NA_BZPLC = B.NA_BZPLC WHERE 1=1
AND A.AUC_OBJ_DSC = #{searchAucObjDsc}
AND B.NA_BZPLCLOC = #{searchPlace}
AND A.AUC_DT BETWEEN TO_CHAR(SYSDATE - 30, 'YYYYMMDD') AND TO_CHAR(SYSDATE, 'YYYYMMDD')
AND A.AUC_DT BETWEEN TO_CHAR(SYSDATE - 20, 'YYYYMMDD') AND TO_CHAR(SYSDATE, 'YYYYMMDD')
AND A.AUC_DT BETWEEN TO_CHAR(SYSDATE - 10, 'YYYYMMDD') AND TO_CHAR(SYSDATE, 'YYYYMMDD')
GROUP BY A.MONTH_OLD_C, B.LOCNM ORDER BY A.MONTH_OLD_C

/* FrontMapper.findAvgPlaceBidAmList */ SELECT A.NA_BZPLCLOC , A.LOCNM , NVL(TRUNC((DECODE(SUM(B.TOT_SBID_CNT),0,0,(SUM(B.SBID_SUM_AMT) / SUM(B.TOT_SBID_CNT))))),0) AS AVG_SBID_AM FROM TB_LA_IS_BM_BZLOC A LEFT JOIN TB_LA_IS_MM_DB_SUM B ON A.NA_BZPLC = B.NA_BZPLC
AND A.NA_BZPLCLOC = #{searchPlace}
AND B.MONTH_OLD_C = #{searchMonthOldC}
AND B.AUC_OBJ_DSC = #{searchAucObjDsc}
AND B.AUC_DT BETWEEN TO_CHAR(SYSDATE - 30, 'YYYYMMDD') AND TO_CHAR(SYSDATE, 'YYYYMMDD')
AND B.AUC_DT BETWEEN TO_CHAR(SYSDATE - 20, 'YYYYMMDD') AND TO_CHAR(SYSDATE, 'YYYYMMDD')
AND B.AUC_DT BETWEEN TO_CHAR(SYSDATE - 10, 'YYYYMMDD') AND TO_CHAR(SYSDATE, 'YYYYMMDD')
WHERE 1=1 GROUP BY A.NA_BZPLCLOC, A.LOCNM

/* FrontMapper.findRecentDateTopList */ SELECT T.* FROM ( SELECT A.NA_BZPLC , C.CLNTNM , A.SRA_INDV_AMNNO , A.AUC_OBJ_DSC , CASE WHEN A.AUC_OBJ_DSC = '1' THEN '송아지' WHEN A.AUC_OBJ_DSC = '2' THEN '비육우' WHEN A.AUC_OBJ_DSC = '3' THEN '번식우' WHEN A.AUC_OBJ_DSC = '0' THEN '일괄' ELSE '' END AS AUC_OBJ_DSC_NM , A.INDV_SEX_C , CASE WHEN A.INDV_SEX_C IN (1,4,6) THEN '암' ELSE '숫' END AS INDV_SEX_C_NM , (SELECT TRUNC(MONTHS_BETWEEN(A.CR_DT, B.BIRTH)) +1 FROM DUAL) AS MONTH_C , A.SBID_AMT , (A.AVG_SBID_AMT - A.SBID_AMT) AS AMT , B.RG_DSC , CASE WHEN B.RG_DSC = '01' THEN '기초' WHEN B.RG_DSC = '02' THEN '혈통' WHEN B.RG_DSC = '03' THEN '고등' WHEN B.RG_DSC = '09' THEN '미등록우' ELSE '없음' END AS RG_DSC_NAME , ROW_NUMBER() OVER(ORDER BY NVL(A.SBID_AMT,0) DESC, B.BIRTH DESC) RANK_NO FROM TB_LA_IS_MM_DB_BDPRTOP A LEFT JOIN TB_LA_IS_MM_INDV B ON A.NA_BZPLC = B.NA_BZPLC AND A.SRA_INDV_AMNNO = B.SRA_INDV_AMNNO INNER JOIN TB_LA_IS_BM_BZLOC C ON A.NA_BZPLC = C.NA_BZPLC WHERE 1=1
AND C.NA_BZPLCLOC = #{searchPlace}
AND A.AUC_OBJ_DSC = #{searchAucObjDsc}
AND A.MONTH_OLD_C = #{searchMonthOldC}
AND A.CR_DT BETWEEN TO_CHAR(SYSDATE-7, 'YYYYMMDD') AND TO_CHAR(SYSDATE-1, 'YYYYMMDD') ) T WHERE 1=1 AND T.RANK_NO < 11 ORDER BY T.RANK_NO
