mapper.select:
SELECT T.* , CASE WHEN TOT_ATDR_CNT !=0 THEN TRUNC((T.SBID_COW_CNT / T.TOT_ATDR_CNT) * 100, 0) ELSE 0 END AS SEL_STS_PERCENT , NVL(B.SIMP_CNM, '합계') AS AUC_OBJ_DSC_NM FROM ( SELECT A.NA_BZPLC , A.AUC_OBJ_DSC , COUNT(CASE WHEN A.SEL_STS_DSC = '22' AND (A.NA_BZPLC, A.TRMN_AMNNO) IN (
(SELECT NA_BZPLC, TRMN_AMNNO FROM TB_LA_IS_MM_MWMN WHERE MB_INTG_NO = #{searchMbIntgNo})
(#{searchNaBzplc}, #{searchTrmnAmnNo})
) THEN 1 END) AS SBID_COW_CNT , COUNT(CASE WHEN B.ATDR_AM > 0 THEN 1 END) AS TOT_ATDR_CNT , COUNT(*) AS TOT_COW_CNT FROM TB_LA_IS_MH_SOG_COW A LEFT OUTER JOIN ( SELECT NA_BZPLC , AUC_OBJ_DSC , AUC_DT , OSLP_NO , TRMN_AMNNO , ATDR_AM , ROW_NUMBER() OVER(PARTITION BY NA_BZPLC, AUC_OBJ_DSC, AUC_DT, OSLP_NO, AUC_PRG_SQ, TRMN_AMNNO ORDER BY ATDR_DTM DESC) RNUM FROM TB_LA_IS_MH_ATDR_LOG LOG WHERE 1=1
AND EXISTS (SELECT 1 FROM TB_LA_IS_MM_MWMN WHERE MB_INTG_NO = #{searchMbIntgNo} AND LOG.NA_BZPLC = NA_BZPLC AND LOG.TRMN_AMNNO = TRMN_AMNNO)
AND LOG.TRMN_AMNNO = #{searchTrmnAmnNo} AND LOG.NA_BZPLC = #{searchNaBzplc}
) B ON A.NA_BZPLC = B.NA_BZPLC AND A.AUC_OBJ_DSC = B.AUC_OBJ_DSC AND A.AUC_DT = B.AUC_DT AND A.OSLP_NO = B.OSLP_NO AND B.RNUM = 1 WHERE 1=1 AND A.AUC_DT LIKE #{searchDateMy} ||'%' AND A.NA_BZPLC = #{searchNaBzplc} GROUP BY ROLLUP(A.NA_BZPLC, A.AUC_OBJ_DSC) HAVING GROUPING_ID(A.NA_BZPLC, A.AUC_OBJ_DSC) IN ('0','1') ) T LEFT OUTER JOIN TB_LA_IS_MH_COMN_APL B ON ( B.SIMP_TPC = 'AUC_OBJ_DSC' AND B.SIMP_C_GRP_SQNO = '7' AND B.SIMP_C = T.AUC_OBJ_DSC ) ORDER BY T.AUC_OBJ_DSC NULLS FIRST
